#!/bin/bash -eREPOS_PATH=/var/svnfunction log {    echo "`date '+%F %T'` $1"}for repo in tools translations gamedev docker; do	if [ ! -d "$REPOS_PATH/$repo" ]; then		log "Initial creation of $repo"		svnadmin create $REPOS_PATH/$repo		if [ $? -ne 0 ]; then			log "Unable to create $repo repo"			continue		fi		echo -e '#!/bin/bash\nexit 0\n' > $REPOS_PATH/$repo/hooks/pre-revprop-change		chmod +x $REPOS_PATH/$repo/hooks/pre-revprop-change		chmod -R g=u /$REPOS_PATH/$repo		svnsync init file://$REPOS_PATH/$repo https://mysvn.ru/consolgames/$repo --username "$1" --password "$2" --no-auth-cache --non-interactive		if [ $? -ne 0 ]; then			log "Unable to initialize $repo repo. Ensure for username and password passed as arguments"			continue		fi	fi	log "Sync $repo..."	svnsync sync file://$REPOS_PATH/$repo --no-auth-cache --non-interactivedone